import React from "react";

class ResetButton extends React.Component {

  reset = () => {
    this.sendToStore(0);
  };

  render () {
    return (
      <div>
        <button className="reset-button" onClick={this.reset}> Reset </button>
      </div>
    )
  }
};

export default ResetButton;
